INSERT INTO `wxstyle` VALUES ('1605', '3', '样式36', ' 		<section  class=\"v3editor\">\r\n<section class=\"pEditor\" data-id=\"1885\">\r\n    <section class=\"I\" style=\"white-space: normal; border: none;  box-sizing: border-box; padding: 10px;\">\r\n        <section class=\"tn-Powered-by-XIUMI\" style=\"width: 5em; height: 5em; float: left; margin: 0px 0.3em 0.3em 0px; display: inline-block; vertical-align: bottom; text-align: center; border-radius: 100%; font-size: 1.2em; font-family: inherit; font-weight: inherit; text-decoration: inherit; color: rgb(255, 255, 255); border-color: rgb(0, 117, 188); box-sizing: border-box; background-color: rgb(0, 117, 188);\">\r\n            <section class=\"tn-Powered-by-XIUMI\" style=\"display: table; width: 100%; height: 5em; box-sizing: border-box;\">\r\n                <section class=\"tn-Powered-by-XIUMI\" style=\"display: table-cell; vertical-align: middle;  line-height: 1.2; box-sizing: border-box;\">\r\n                    <p style=\"box-sizing: border-box;\">\r\n                        刘翔\r\n                    </p>\r\n                    <p style=\"box-sizing: border-box;\">\r\n                        <span style=\"font-size: 12px;\">田径运动员</span>\r\n                    </p>\r\n                </section>\r\n            </section>\r\n        </section>\r\n        <section class=\"pBrush\" style=\"margin-top: 1em; font-size: 0.9em; font-family: inherit; font-weight: inherit; text-decoration: inherit; box-sizing: border-box;\">\r\n            <section class=\"tn-Powered-by-XIUMI\" style=\"box-sizing: border-box;\">\r\n                <strong><span style=\"font-size: 20px;\">刘翔葛天离婚</span></strong>\r\n            </section>\r\n            <section class=\"tn-Powered-by-XIUMI\" style=\"box-sizing: border-box;\">\r\n                <br class=\"tn-Powered-by-XIUMI\" style=\"box-sizing: border-box;\"/>\r\n            </section>\r\n            <section class=\"tn-Powered-by-XIUMI\" style=\"box-sizing: border-box;\">\r\n                <span style=\"font-size: 16px;\">2015年6月25日，刘翔在微博宣布与演员妻子葛天离婚。他在微博自曝与葛天相恋4个月后结婚，现在两人因性格不合而离婚。</span>\r\n            </section>\r\n            <p style=\"box-sizing: border-box;\">\r\n                <br class=\"tn-Powered-by-XIUMI\" style=\"box-sizing: border-box;\"/>\r\n            </p>\r\n            <p style=\"box-sizing: border-box;\">\r\n                <span style=\"font-size: 16px;\">昨天下午4:22分，刘翔发布了一条微博：“我与葛天2014年5月恋爱，同年9月结婚，婚后因性格不合，于今天结束这段婚姻，希望今后各自都有更美好的人生，祝愿彼此。”<span style=\"color: rgb(0, 112, 192);\"></span></span>\r\n            </p>\r\n            <p style=\"box-sizing: border-box;\">\r\n                <span style=\"font-size: 16px;\">这一天，距离刘翔正式领证结婚，只有290天。这段短暂的婚姻，几乎没有蜜月期，甚至没有婚礼。</span>\r\n            </p>\r\n            <p style=\"box-sizing: border-box;\">\r\n                <span style=\"font-size: 16px;\"><br/></span>\r\n            </p>\r\n            <p style=\"box-sizing: border-box;\">\r\n                <span style=\"font-size: 16px;\"></span>\r\n            </p>\r\n            <section class=\"tn-Powered-by-XIUMI\" style=\"border: 0px; box-sizing: border-box; padding: 0px;\">\r\n                <section class=\"tn-Powered-by-XIUMI\" style=\"margin: 0.5em 0px; border-top-style: dashed; border-top-width: 1px; border-color: rgb(0, 117, 188); box-sizing: border-box;\"></section>\r\n                <section style=\"width: 0px; height: 0px; clear: both;\"></section>\r\n            </section>\r\n            <p style=\"box-sizing: border-box;\">\r\n                <span style=\"font-size: 16px;\"></span>\r\n            </p>\r\n        </section>\r\n    </section>\r\n</section>\r\n\r\n\r\n</section>\r\n\r\n		', '0', '2015-09-06 13:11:43');
