INSERT INTO `wxstyle` VALUES ('1580', '8', '样式36', ' 		<section  class=\"v3editor\">\r\n<section style=\"width:100%;border: 1px solid rgb(198,198,199);margin: 0;border-radius:10px;\"><section class=\"indoorsteam\" style=\"margin:15px 10px;\"><span style=\"font-size:24px\"><strong>Share&nbsp;</strong></span><span style=\"font-size:14px\">共享才能双赢</span></section><img src=\"https://mmbiz.qlogo.cn/mmbiz/cZV2hRpuAPhzNa7pxpyCcER50LzzibU34bpuI5a8yHIC2lgjyu2U9KI56WGUyiaDzUdvESP9LrBwBdu2icmEBwJbg/0\" style=\"margin-top:5px; width:100%\"><section data-style=\"margin: 5px 0px; font-size: 14px; padding:5px; color: rgb(198,198,199); text-align: left; line-height: 1.5;\" style=\"margin:15px;\"><p style=\"margin: 5px 0px; font-size: 14px; padding:5px; color: rgb(198,198,199); text-align:left; line-height: 1.5;\">“双赢”来自于英文：“win——win”的中文翻译。营销学这样认为，双赢是成双的，对于客户与企业来说，应是客户先赢企业后赢；对于员工与企业之间来说，应是员工先赢企业后赢。双赢强调的是双方的利益兼顾，即所谓的“赢者不全赢，输者不全输”。</p></section></section>\r\n</section>\r\n\r\n		', '0', '2015-09-06 12:18:37');
