INSERT INTO `wxstyle` VALUES ('1596', '8', '样式36', ' 		<section  class=\"v3editor\">\r\n<section class=\"pEditor\" data-id=\"1813\"><blockquote style=\"max-width: 100%; padding: 5px 15px; border: none rgb(255, 129, 36); margin: 0px; word-wrap: break-word !important; box-sizing: border-box !important; background-image: url(https://mmbiz.qlogo.cn/mmbiz/yqVAqoZvDibGBk0icXPWgOsicXKcLCemXGTIRz9XALq57sg3uEVsQfCl22l7DOm0szTCVGSgz94nwib5GJ9XajoCQA/0?wx_fmt=jpeg); background-size: cover;background-position: 0% 50%;background-repeat: no-repeat;\"><section style=\"padding:5px;\"><p style=\"margin-top: 0px; margin-bottom: 0px; max-width: 100%; word-wrap: normal; min-height: 1em; white-space: pre-wrap; line-height: 1.75em; text-align: center; color: inherit; padding: 0px; box-sizing: border-box !important;\"><span style=\"box-sizing:border-box; color:rgb(192, 0, 0); font-size:18px; margin:0px; padding:0px\">信纸背景</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; max-width: 100%; word-wrap: normal; min-height: 1em; white-space: pre-wrap; line-height: 1.75em; color: inherit; padding: 0px; box-sizing: border-box !important; text-align: center;\"><span style=\"color:rgb(127, 127, 127); line-height:2em\">平静的日子</span></p><p style=\"text-align: center; line-height: 2em;\"><span style=\"color:rgb(127, 127, 127)\">汨罗江沉默着</span></p><p style=\"text-align: center; line-height: 2em;\"><span style=\"color:rgb(127, 127, 127)\">　　偷偷隐藏千百年前的疼痛</span></p><p style=\"text-align: center; line-height: 2em;\"><span style=\"color:rgb(127, 127, 127)\">几乎静止般流动的水</span></p><p style=\"text-align: center; line-height: 2em;\"><span style=\"color:rgb(127, 127, 127)\">是谁遗落的</span></p><p style=\"text-align: center; line-height: 2em;\"><span style=\"color:rgb(127, 127, 127)\">麻绳或白绫</span></p><p style=\"text-align: center; line-height: 2em;\"><span style=\"color:rgb(127, 127, 127)\">端午节</span></p><p style=\"text-align: center; line-height: 2em;\"><span style=\"color:rgb(127, 127, 127)\">汨罗江在雨水中沸腾</span></p><p style=\"text-align: center; line-height: 2em;\"><span style=\"color:rgb(127, 127, 127)\">为一个永恒的名字</span></p><p style=\"text-align: center; line-height: 2em;\"><span style=\"color:rgb(127, 127, 127)\">粽子在水中沉浮</span></p><p style=\"text-align: center; line-height: 2em;\"><span style=\"color:rgb(127, 127, 127)\">鱼儿慢摇九歌</span></p><p style=\"text-align: center; line-height: 2em;\"><span style=\"color:rgb(127, 127, 127)\">两岸离骚声声</span></p><p style=\"line-height: 2em; text-align: center;\"><span style=\"color:rgb(127, 127, 127)\">......</span></p></section></blockquote></section>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n</section>\r\n\r\n		', '0', '2015-09-06 12:24:25');
